import {
  Column,
  UseSortByOptions,
  useTable,
  useSortBy,
  usePagination,
  TableInstance,
  UsePaginationInstanceProps,
  TableState,
  UsePaginationState,
  HeaderGroup,
  UseSortByColumnProps,
} from "react-table";

export type columnOption<D extends object> = Column<D> & UseSortByOptions<D>;

const List = <D extends object>({
  columns,
  data,
}: {
  columns: columnOption<D>[];
  data: D[];
}) => {
  const table = useTable<D>(
    {
      columns,
      data,
    },
    useSortBy,
    usePagination
  ) as TableInstance<D> &
    UsePaginationInstanceProps<D> & {
      state: TableState<D> & UsePaginationState<D>;
    };
  const { headerGroups } = table;
  return (
    <div>
      <thead>
        {headerGroups.map((headerGroup, headerGroupIndex) => (
          <tr {...headerGroup.getHeaderGroupProps()} key={headerGroupIndex}>
            {(
              headerGroup.headers as (HeaderGroup<D> &
                UseSortByColumnProps<D>)[]
            ).map((column) => (
              <div></div>
            ))}
          </tr>
        ))}
      </thead>
    </div>
  );
};

export default List;
