import * as React from "react";
import { render } from "react-dom";

import { useTable, Column } from "react-table";
import styled from "styled-components";
import "./App.css";

interface Data {
  artist: string;
  song: string;
  cost: string;
  sample: string;
}

const columns: Column<Data>[] = [
  {
    Header: "歌手",
    accessor: "artist",
    width: "100px",
  },
  {
    Header: "曲",
    accessor: "song",
    width: "300px",
  },
  {
    Header: "値段",
    accessor: "cost",
    width: "300px",
  },
  {
    Header: "サンプル",
    accessor: "sample",
    width: "50px",
  },
];

const data: Data[] = [
  {
    artist: "乃木坂46",
    song: "さよならの意味",
    cost: "100000",
    sample: "",
  },
  {
    artist: "乃木坂45",
    song: "さよならの意味",
    cost: "100000",
    sample: "",
  },
  {
    artist: "乃木坂44",
    song: "おはようの意味",
    cost: "100000",
    sample: "",
  },
  {
    artist: "乃木坂43",
    song: "こんにちはの意味",
    cost: "100000",
    sample: "",
  },
  {
    artist: "乃木坂42",
    song: "たかなわの意味",
    cost: "100000",
    sample: "",
  },
];

function Table() {
  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
    useTable<Data>({ columns, data });

  console.log(getTableProps);
  console.log(getTableBodyProps);
  console.log(headerGroups);
  console.log(rows);
  console.log(prepareRow);

  return (
    <StyledTable {...getTableProps()}>
      <StyledTableHeader>
        {headerGroups.map((headerGroup) => (
          <tr {...headerGroup.getHeaderGroupProps()}>
            {headerGroup.headers.map((column) => (
              <StyledTableColumn
                // width={column.width}
                width="300px"
              >
                {column.render("Header")}
              </StyledTableColumn>
            ))}
          </tr>
        ))}
      </StyledTableHeader>
      <StyledTableBody>
        <tbody {...getTableBodyProps()}>
          {rows.map((row, i) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map((cell) => {
                  return (
                    <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
                  );
                })}
              </tr>
            );
          })}
        </tbody>
      </StyledTableBody>
    </StyledTable>
  );
}

const StyledTable = styled.div`
  width: 80%;
  height: auto;
  boder: gray 1px solid;
`;
const StyledTableHeader = styled.div`
  width: 80%;
  height: auto;
  boder: gray 1px solid;
`;
const StyledTableBody = styled.div`
  width: 80%;
  boder: gray 1px solid;
`;

export const StyledTableColumn = styled.div<{
  width?: string;
  margin?: string;
  textAlign?: string;
}>`
  display: inline;
  width: ${(props): string => (props.width ? props.width : "160px")};
  height: 40px;
  padding: 0 12px;
  color: black;
  font-size: 16px;

  :focus,
  :active {
    border: 1px solid pink;
  }
`;
export default Table;
