import { useCallback } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import React from "react";

const Calender: React.FC<CalenderProps> = (props) => {
  const clickDate = useCallback((date) => {
    props.setStartDay(date);
  }, []);

  const startDate = new Date();
  console.log(startDate);

  const parseAsMoment = (dateTimeStr) => {
    return moment.utc(dateTimeStr, "YYYY-MM-DDTHH:mm:00Z", "ja").utcOffset(9);
  };

  const toUtcIso8601str = (momentInstance) => {
    return momentInstance.clone().utc().format("YYYY-MM-DDTHH:mm:00Z");
  };
  return (
    <React.Fragment>
      <DatePicker
        selected={props.startDate}
        onChange={clickDate}
        customInput={
          <button>{parseAsMoment(startDate).format("YYYY/MM/DD")}</button>
        }
      />
      ;
    </React.Fragment>
  );
};
export type CalenderProps = {
  startDate: Date;
  setStartDay: (startDate: Date) => void;
};

export default Calender;
